class NeonButton{constructor(selector,options={}){this.elements=typeof selector==='string' ? document.querySelectorAll(selector):[selector];this.config={type:options.type||'rose',size:options.size||'normal',icon:options.icon||null,text:options.text||null,disabled:options.disabled||false,onClick:options.onClick||null};this.init();}init(){this.elements.forEach(element=>{this.setupElement(element);this.addEventListeners(element);});}setupElement(element){element.classList.add('neon-btn');element.classList.add(`neon-btn-${this.config.type}`);if(this.config.size!=='normal'){element.classList.add(`neon-btn-${this.config.size}`);}if(this.config.icon){element.classList.add('neon-btn-icon');if(this.config.icon!=='default'){element.classList.add(`icon-${this.config.icon}`);}}if(this.config.text){element.textContent=this.config.text;}if(this.config.disabled){element.disabled=true;}if(element.tagName==='BUTTON'||element.tagName==='INPUT'){element.type=element.type||'button';}if(!element.getAttribute('role')&&element.tagName==='A'){element.setAttribute('role','button');}}addEventListeners(element){if(this.config.onClick){element.addEventListener('click',(e)=>{if(!element.disabled){this.config.onClick(e,element);}});}element.addEventListener('mouseenter',()=>{if(!element.disabled){this.onHoverEnter(element);}});element.addEventListener('mouseleave',()=>{if(!element.disabled){this.onHoverLeave(element);}});element.addEventListener('touchstart',()=>{if(!element.disabled){this.onTouchStart(element);}});element.addEventListener('touchend',()=>{if(!element.disabled){this.onTouchEnd(element);}});}onHoverEnter(element){element.style.transform='translateY(-3px)';if(this.config.type==='rose'){element.style.boxShadow='0 0 35px rgba(232,180,184,0.7),0 12px 30px rgba(232,180,184,0.4)';}else{element.style.boxShadow='0 0 35px rgba(13,115,119,0.7),0 12px 30px rgba(13,115,119,0.4)';}}onHoverLeave(element){element.style.transform='';element.style.boxShadow='';}onTouchStart(element){element.style.transform='translateY(-1px)';}onTouchEnd(element){element.style.transform='';}updateText(newText){this.elements.forEach(element=>{element.textContent=newText;});}updateType(newType){this.elements.forEach(element=>{element.classList.remove(`neon-btn-${this.config.type}`);element.classList.add(`neon-btn-${newType}`);});this.config.type=newType;}disable(){this.config.disabled=true;this.elements.forEach(element=>{element.disabled=true;element.classList.add('neon-btn-disabled');});}enable(){this.config.disabled=false;this.elements.forEach(element=>{element.disabled=false;element.classList.remove('neon-btn-disabled');});}destroy(){this.elements.forEach(element=>{element.classList.remove('neon-btn',`neon-btn-${this.config.type}`,`neon-btn-${this.config.size}`,'neon-btn-icon',`icon-${this.config.icon}`);element.style.transform='';element.style.boxShadow='';const newElement=element.cloneNode(true);element.parentNode.replaceChild(newElement,element);});}static createButton(text,type='rose',options={}){const button=document.createElement('button');button.textContent=text;return new NeonButton(button,{...options,type});}static initializeAll(selector='[data-neon-button]'){const elements=document.querySelectorAll(selector);const buttons=[];elements.forEach(element=>{const config={type:element.dataset.neonType||'rose',size:element.dataset.neonSize||'normal',icon:element.dataset.neonIcon||null,disabled:element.hasAttribute('disabled')};buttons.push(new NeonButton(element,config));});return buttons;}}document.addEventListener('DOMContentLoaded',()=>{NeonButton.initializeAll();});if(typeof module!=='undefined'&&module.exports){module.exports=NeonButton;}if(typeof window!=='undefined'){window.NeonButton=NeonButton;}